using System;
using System.Collections.Generic;
using NUnit.Framework;
using LunarDoggo.FileSystemTree;

namespace FileSystemTreeTest
{
    public class FileSystemTreeItemTest
    {
        [Test]
        public void TestFileSystemTreeItemWithValidParameters()
        {
            var children = new List<FileSystemTreeItem>
            {
                new FileSystemTreeItem("child1", FileSystemTreeItemType.File, null),
                new FileSystemTreeItem("child2", FileSystemTreeItemType.File, null)
            };

            var item = new FileSystemTreeItem("parent", FileSystemTreeItemType.Directory, children);

            Assert.AreEqual("parent", item.name);
            Assert.AreEqual(FileSystemTreeItemType.Directory, item.type);
            Assert.AreEqual(children, item.children);
        }

        [Test]
        public void TestFileSystemTreeItemWithNullChildren()
        {
            var item = new FileSystemTreeItem("item", FileSystemTreeItemType.File, null);

            Assert.AreEqual("item", item.name);
            Assert.AreEqual(FileSystemTreeItemType.File, item.type);
            Assert.IsNull(item.children);
        }

        [Test]
        public void TestFileSystemTreeItemWithEmptyName()
        {
            var item = new FileSystemTreeItem("", FileSystemTreeItemType.File, null);

            Assert.AreEqual("", item.name);
            Assert.AreEqual(FileSystemTreeItemType.File, item.type);
            Assert.IsNull(item.children);
        }
    }
}
