using System;
using System.Collections.Generic;
using NUnit.Framework;
using LunarDoggo.FileSystemTree;

namespace FileSystemTreeTests
{
    public class FileSystemTreeItemTests
    {
        [Test]
        public void TestFileSystemTreeItemInitialization()
        {
            var children = new List<FileSystemTreeItem>
            {
                new FileSystemTreeItem("Child1", FileSystemTreeItemType.File, new List<FileSystemTreeItem>()),
                new FileSystemTreeItem("Child2", FileSystemTreeItemType.Directory, new List<FileSystemTreeItem>())
            };

            var item = new FileSystemTreeItem("TestItem", FileSystemTreeItemType.Directory, children);

            Assert.AreEqual("TestItem", item.Name);
            Assert.AreEqual(FileSystemTreeItemType.Directory, item.Type);
            Assert.AreEqual(children, item.Children);
        }

        [Test]
        public void TestFileSystemTreeItemInitializationWithNoChildren()
        {
            var item = new FileSystemTreeItem("TestItem", FileSystemTreeItemType.File, new List<FileSystemTreeItem>());

            Assert.AreEqual("TestItem", item.Name);
            Assert.AreEqual(FileSystemTreeItemType.File, item.Type);
            Assert.AreEqual(0, item.Children.Count);
        }
    }
