namespace FileSystemTree.Tests
{
    public class FileSystemTreeTests
    {
        [Test]
        public void TestOutputFileSystemTreeLevelWithRootItem()
        {
            FileSystemTreeItem rootItem = new FileSystemTreeItem("root", "folder", ImmutableList<FileSystemTreeItem>.Empty);
            using (StringWriter sw = new StringWriter())
            {
                Console.SetOut(sw);
                Program.OutputFileSystemTreeLevel(0, rootItem);
