        [Test]
        public void TestOutputFileSystemTreeLevelWithChildItems()
        {
            FileSystemTreeItem childItem1 = new FileSystemTreeItem("child1", "file", ImmutableList<FileSystemTreeItem>.Empty);
            FileSystemTreeItem childItem2 = new FileSystemTreeItem("child2", "file", ImmutableList<FileSystemTreeItem>.Empty);
            FileSystemTreeItem rootItem = new FileSystemTreeItem("root", "folder", ImmutableList.Create(childItem1, childItem2));
            using (StringWriter sw = new StringWriter())
            {
                Console.SetOut(sw);
                Program.OutputFileSystemTreeLevel(0, rootItem);
                string expected = "root (folder)\n  child1 (file)\n  child2 (file)\n";
                Assert.AreEqual(expected, sw.ToString());
            }
        }
